import _ from 'lodash';
import { Input, Tooltip } from 'antd';
import React from 'react';

const searchTypeList: any = [
    {
        key: 'caseSensitive',
        imgSrc: 'data:image/png;base64,',
        tip: '区分大小写匹配',
    },
    {
        key: 'precise',
        imgSrc: 'data:image/png;base64,',
        tip: '精确匹配',
    },
    {
        key: 'front',
        imgSrc: 'data:image/png;base64,',
        tip: '头部匹配',
    },
    {
        key: 'tail',
        imgSrc: 'data:image/png;base64,',
        tip: '尾部匹配',
    },
];

export type SearchType = 'fuzzy' | 'precise' | 'front' | 'tail';

export interface MultiSearchInputProps {
    placeholder?: string;
    style?: React.CSSProperties;
    value?: string; // input框的值
    onChange?: (value: string) => void;
    onSearch?: (value: string, searchType: SearchType) => void;
    onTypeChange?: (searchType: SearchType) => void;
    searchType?: SearchType; // input框中选中的筛选方式
    filterOptions?: SearchType[]; // 数组
    className?: string;
    [propName: string]: any;
}

export interface MultiSearchInputStates {}
class MultiSearchInput extends React.Component<
    MultiSearchInputProps,
    MultiSearchInputStates & MultiSearchInputProps
> {
    constructor(props: any) {
        super(props);
        this.state = {
            placeholder: this.props.placeholder || '',
            style: this.props.style || {},
            value: this.props.value || '',
            onChange:
                this.props.onChange ||
                ((value: any) => {
                    console.log(value);
                }),
            onSearch:
                this.props.onSearch ||
                ((value: any, searchType: any) => {
                    console.log(value, searchType);
                }),
            onTypeChange:
                this.props.onTypeChange ||
                ((searchType: any) => {
                    console.log(searchType);
                }),
            searchType: this.props.searchType || 'fuzzy',
            filterOptions: this.props.filterOptions || ['precise', 'front', 'tail'],
        };
    }

    render() {
        const { placeholder, style, value, onChange, onSearch, onTypeChange, filterOptions } =
            this.state;
        const { className = '', value: propsValue, searchType: propsSearchType } = this.props;
        let searchType = this.state.searchType;
        searchType = propsSearchType != null ? propsSearchType : searchType;
        const filterList = _.filter(searchTypeList, (item: any) => {
            return _.includes(filterOptions, item.key);
        });
        return (
            <div
                style={{
                    position: 'relative',
                    width: '250px',
                }}
                className={className}
            >
                <Input
                    data-testid="input"
                    value={propsValue != null ? propsValue : value}
                    placeholder={placeholder}
                    style={{
                        ...style,
                        paddingRight: `${filterOptions.length * 26 + 5}px`,
                    }}
                    onChange={(e: any) => {
                        this.setState({ value: e.target.value });
                        onChange(e.target.value);
                    }}
                    onPressEnter={(e: any) => {
                        onSearch(e.target.value, searchType);
                    }}
                />
                <div
                    style={{
                        position: 'absolute',
                        height: '100%',
                        top: '0px',
                        right: '0px',
                        width: `${filterOptions.length * 26 + 5}px`,
                        display: 'flex',
                        justifyContent: 'space-around',
                        alignItems: 'center',
                        paddingRight: '5px',
                    }}
                >
                    {_.map(filterList, (item: any) => {
                        return (
                            <div
                                data-testid={`icon-${item.key}`}
                                key={`icon-${item.key}`}
                                style={{
                                    cursor: 'pointer',
                                    display: 'block',
                                    height: '22px',
                                    width: '26px',
                                    border: searchType === item.key ? '1px solid #2491F7' : 'none',
                                    overflow: 'hidden',
                                }}
                                onClick={() => {
                                    const newSearchType =
                                        searchType === item.key ? 'fuzzy' : item.key;
                                    this.setState({
                                        searchType: newSearchType,
                                    });
                                    onTypeChange(newSearchType);
                                }}
                            >
                                <Tooltip title={item.tip} mouseEnterDelay={0.5}>
                                    <img
                                        src={item.imgSrc}
                                        style={{
                                            marginTop: '-6px',
                                            marginLeft: '-3px',
                                        }}
                                    />
                                </Tooltip>
                            </div>
                        );
                    })}
                </div>
            </div>
        );
    }
}

export default MultiSearchInput;
